glsl1.vert:
precision highp float;
struct StandardVertInput {
  highp vec4 position;
  vec3 normal;
  vec4 tangent;
};
attribute vec3 a_position;
attribute vec3 a_normal;
attribute vec4 a_tangent;
#if CC_USE_MORPH
    attribute float a_vertexId;
    int getVertexId() {
        return int(a_vertexId);
    }
uniform vec4 cc_displacementWeights[15];
uniform vec4 cc_displacementTextureInfo;
    vec4 fetchFlatPixel(sampler2D tex, int pixelIndex) {
        float pixelIndexF = float(pixelIndex);
        vec2 textureResolution = vec2(float(cc_displacementTextureInfo.x), float(cc_displacementTextureInfo.y));
        float pixelX = mod(pixelIndexF, textureResolution.x);
        float pixelY = floor(pixelIndexF / textureResolution.x);
        vec2 uv = (vec2(pixelX, pixelY) + .5) / textureResolution;
        return texture2D(tex, uv);
    }
float getDisplacementWeight(int index) {
    float m = mod(float(index), 4.0);
    if (m < 1.0) {
        return cc_displacementWeights[index / 4].x;
    } else if (m < 2.0) {
        return cc_displacementWeights[index / 4].y;
    } else if (m < 3.0) {
        return cc_displacementWeights[index / 4].z;
    } else {
        return cc_displacementWeights[index / 4].w;
    }
}
vec3 getVec3DisplacementFromTexture(sampler2D tex, int vertexIndex) {
#if CC_MORPH_PRECOMPUTED
    return fetchFlatPixel(tex, vertexIndex).rgb;
#else
    vec3 result = vec3(0, 0, 0);
    for (int iTarget = 0; iTarget < CC_MORPH_TARGET_COUNT; ++iTarget) {
        int dataPixelStart = int(fetchFlatPixel(tex, iTarget).r);
        result += (fetchFlatPixel(tex, dataPixelStart + vertexIndex).rgb * getDisplacementWeight(iTarget));
    }
    return result;
#endif
}
#if CC_MORPH_TARGET_HAS_POSITION
    uniform sampler2D cc_PositionDisplacements;
    vec3 getPositionDisplacement(int vertexId) {
        return getVec3DisplacementFromTexture(cc_PositionDisplacements, vertexId);
    }
#endif
#if CC_MORPH_TARGET_HAS_NORMAL
    uniform sampler2D cc_NormalDisplacements;
    vec3 getNormalDisplacement(int vertexId) {
        return getVec3DisplacementFromTexture(cc_NormalDisplacements, vertexId);
    }
#endif
#if CC_MORPH_TARGET_HAS_TANGENT
    uniform sampler2D cc_TangentDisplacements;
    vec3 getTangentDisplacement(int vertexId) {
        return getVec3DisplacementFromTexture(cc_TangentDisplacements, vertexId);
    }
#endif
void applyMorph (inout StandardVertInput attr) {
    int vertexId = getVertexId();
#if CC_MORPH_TARGET_HAS_POSITION
    attr.position.xyz = attr.position.xyz + getPositionDisplacement(vertexId);
#endif
#if CC_MORPH_TARGET_HAS_NORMAL
    attr.normal.xyz = attr.normal.xyz + getNormalDisplacement(vertexId);
#endif
#if CC_MORPH_TARGET_HAS_TANGENT
    attr.tangent.xyz = attr.tangent.xyz + getTangentDisplacement(vertexId);
#endif
}
void applyMorph (inout vec4 position) {
#if CC_MORPH_TARGET_HAS_POSITION
    position.xyz = position.xyz + getPositionDisplacement(getVertexId());
#endif
}
#endif
#if CC_USE_SKINNING
attribute vec4 a_weights;
attribute vec4 a_joints;
highp float decode32 (highp vec4 rgba) {
  rgba = rgba * 255.0;
  highp float Sign = 1.0 - step(127.99, rgba[3]) * 2.0;
  highp float Exponent = 2.0 * mod(rgba[3], 127.99) + step(128.0, rgba[2]) - 127.0;
  highp float Mantissa = mod(rgba[2], 128.0) * 65536.0 + rgba[1] * 256.0 + rgba[0] + 8388608.0;
  return Sign * exp2(Exponent - 23.0) * Mantissa;
}
#if CC_USE_BAKED_ANIMATION
  #if USE_INSTANCING
    attribute highp vec4 a_jointAnimInfo;
  #endif
  uniform highp vec4 cc_jointTextureInfo;
  uniform highp vec4 cc_jointAnimInfo;
  uniform sampler2D cc_jointTexture;
  #else
  uniform highp vec4 cc_joints[90];
#endif
#if CC_USE_BAKED_ANIMATION
  #if CC_SUPPORT_FLOAT_TEXTURE
    mat4 getJointMatrix (float i) {
    #if USE_INSTANCING
      highp float j = 3.0 * (a_jointAnimInfo.x * a_jointAnimInfo.y + i) + a_jointAnimInfo.z;
    #else
      highp float j = 3.0 * (cc_jointAnimInfo.x * cc_jointTextureInfo.y + i) + cc_jointTextureInfo.z;
    #endif
    highp float invSize = cc_jointTextureInfo.w;
    highp float y = floor(j * invSize);
    highp float x = j - y * cc_jointTextureInfo.x;
    y = (y + 0.5) * invSize;
      vec4 v1 = texture2D(cc_jointTexture, vec2((x + 0.5) * invSize, y));
      vec4 v2 = texture2D(cc_jointTexture, vec2((x + 1.5) * invSize, y));
      vec4 v3 = texture2D(cc_jointTexture, vec2((x + 2.5) * invSize, y));
      return mat4(vec4(v1.xyz, 0.0), vec4(v2.xyz, 0.0), vec4(v3.xyz, 0.0), vec4(v1.w, v2.w, v3.w, 1.0));
    }
  #else
    mat4 getJointMatrix (float i) {
    #if USE_INSTANCING
      highp float j = 12.0 * (a_jointAnimInfo.x * a_jointAnimInfo.y + i) + a_jointAnimInfo.z;
    #else
      highp float j = 12.0 * (cc_jointAnimInfo.x * cc_jointTextureInfo.y + i) + cc_jointTextureInfo.z;
    #endif
    highp float invSize = cc_jointTextureInfo.w;
    highp float y = floor(j * invSize);
    highp float x = j - y * cc_jointTextureInfo.x;
    y = (y + 0.5) * invSize;
      vec4 v1 = vec4(
        decode32(texture2D(cc_jointTexture, vec2((x + 0.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 1.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 2.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 3.5) * invSize, y)))
      );
      vec4 v2 = vec4(
        decode32(texture2D(cc_jointTexture, vec2((x + 4.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 5.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 6.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 7.5) * invSize, y)))
      );
      vec4 v3 = vec4(
        decode32(texture2D(cc_jointTexture, vec2((x + 8.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 9.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 10.5) * invSize, y))),
        decode32(texture2D(cc_jointTexture, vec2((x + 11.5) * invSize, y)))
      );
      return mat4(vec4(v1.xyz, 0.0), vec4(v2.xyz, 0.0), vec4(v3.xyz, 0.0), vec4(v1.w, v2.w, v3.w, 1.0));
    }
  #endif
#else
  mat4 getJointMatrix (float i) {
    int idx = int(i);
    vec4 v1 = cc_joints[idx * 3];
    vec4 v2 = cc_joints[idx * 3 + 1];
    vec4 v3 = cc_joints[idx * 3 + 2];
    return mat4(vec4(v1.xyz, 0.0), vec4(v2.xyz, 0.0), vec4(v3.xyz, 0.0), vec4(v1.w, v2.w, v3.w, 1.0));
  }
#endif
mat4 skinMatrix () {
  return getJointMatrix(a_joints.x) * a_weights.x
       + getJointMatrix(a_joints.y) * a_weights.y
       + getJointMatrix(a_joints.z) * a_weights.z
       + getJointMatrix(a_joints.w) * a_weights.w;
}
void CCSkin (inout vec4 position) {
  mat4 m = skinMatrix();
  position = m * position;
}
void CCSkin (inout StandardVertInput attr) {
  mat4 m = skinMatrix();
  attr.position = m * attr.position;
  attr.normal = (m * vec4(attr.normal, 0.0)).xyz;
  attr.tangent.xyz = (m * vec4(attr.tangent.xyz, 0.0)).xyz;
}
#endif
uniform highp mat4 cc_matView;
uniform highp mat4 cc_matProj;
#if USE_INSTANCING
  attribute vec4 a_matWorld0;
  attribute vec4 a_matWorld1;
  attribute vec4 a_matWorld2;
#if USE_LIGHTMAP
  attribute vec4 a_lightingMapUVParam;
#endif
#elif USE_BATCHING
  attribute float a_dyn_batch_id;
  uniform highp mat4 cc_matWorlds[10];
#else
uniform highp mat4 cc_matWorld;
uniform highp mat4 cc_matWorldIT;
uniform highp vec4 cc_lightingMapUVParam;
#endif
uniform vec4 tilingOffset;
#if USE_VERTEX_COLOR
  attribute vec3 a_color;
  varying vec3 v_color;
#endif
varying vec3 v_position;
varying vec3 v_normal;
#if USE_NORMAL_MAP
  varying vec3 v_tangent;
  varying vec3 v_bitangent;
#endif
attribute vec2 a_texCoord;
varying vec2 v_uv;
attribute vec2 a_texCoord1;
varying vec2 v_uv1;
#if USE_LIGHTMAP && !USE_BATCHING
  varying vec2 v_luv;
#endif
vec4 vert () {
  StandardVertInput In;
  In.position = vec4(a_position, 1.0);
  In.normal = a_normal;
  In.tangent = a_tangent;
  #if CC_USE_MORPH
    applyMorph(In);
  #endif
  #if CC_USE_SKINNING
    CCSkin(In);
  #endif
  mat4 matWorld, matWorldIT;
  #if USE_INSTANCING
    matWorld = mat4(
      vec4(a_matWorld0.xyz, 0.0),
      vec4(a_matWorld1.xyz, 0.0),
      vec4(a_matWorld2.xyz, 0.0),
      vec4(a_matWorld0.w, a_matWorld1.w, a_matWorld2.w, 1.0)
    );
    matWorldIT = matWorld;
  #elif USE_BATCHING
    matWorld = cc_matWorlds[int(a_dyn_batch_id)];
    matWorldIT = matWorld;
  #else
    matWorld = cc_matWorld;
    matWorldIT = cc_matWorldIT;
  #endif
  vec4 pos = matWorld * In.position;
  v_position = pos.xyz;
  v_normal = normalize((matWorldIT * vec4(In.normal, 0.0)).xyz);
  #if USE_NORMAL_MAP
    v_tangent = normalize((matWorld * vec4(In.tangent.xyz, 0.0)).xyz);
    v_bitangent = cross(v_normal, v_tangent) * In.tangent.w;
  #endif
  v_uv = a_texCoord * tilingOffset.xy + tilingOffset.zw;
  #if HAS_SECOND_UV
    v_uv1 = a_texCoord1 * tilingOffset.xy + tilingOffset.zw;
  #endif
  #if USE_VERTEX_COLOR
    v_color = a_color;
  #endif
  #if USE_LIGHTMAP && HAS_SECOND_UV && !USE_BATCHING && !USE_INSTANCING
    v_luv = cc_lightingMapUVParam.xy + a_texCoord1 * cc_lightingMapUVParam.zw;
  #endif
  #if USE_LIGHTMAP && HAS_SECOND_UV && !USE_BATCHING && USE_INSTANCING
    v_luv = a_lightingMapUVParam.xy + a_texCoord1 * a_lightingMapUVParam.zw;
  #endif
  return cc_matProj * (cc_matView * matWorld) * In.position;
}
void main() { gl_Position = vert(); }

glsl1.frag:
  #ifdef GL_EXT_shader_texture_lod
    #extension GL_EXT_shader_texture_lod : enable
  #endif
precision highp float;
uniform highp vec4 cc_cameraPos;
uniform mediump vec4 cc_exposure;
uniform mediump vec4 cc_mainLitDir;
uniform mediump vec4 cc_mainLitColor;
uniform mediump vec4 cc_ambientSky;
uniform mediump vec4 cc_ambientGround;
#if CC_USE_IBL
uniform samplerCube cc_environment;
vec3 unpackRGBE (vec4 rgbe) {
  return rgbe.rgb * pow(2.0, rgbe.a * 255.0 - 128.0);
}
vec4 fragTextureLod (sampler2D tex, vec2 coord, float lod) {
    #ifdef GL_EXT_shader_texture_lod
      return texture2DLodEXT(tex, coord, lod);
    #else
      return texture2D(tex, coord, lod);
    #endif
}
vec4 fragTextureLod (samplerCube tex, vec3 coord, float lod) {
    #ifdef GL_EXT_shader_texture_lod
      return textureCubeLodEXT(tex, coord, lod);
    #else
      return textureCube(tex, coord, lod);
    #endif
}
#endif
vec3 SRGBToLinear (vec3 gamma) {
  return gamma * gamma;
}
uniform highp vec4 cc_sphereLitPos[2];
uniform vec4 cc_sphereLitSizeRange[2];
uniform vec4 cc_sphereLitColor[2];
uniform highp vec4 cc_spotLitPos[2];
uniform vec4 cc_spotLitSizeRangeAngle[2];
uniform vec4 cc_spotLitDir[2];
uniform vec4 cc_spotLitColor[2];
float SmoothDistAtt (float distSqr, float invSqrAttRadius) {
  float factor = distSqr * invSqrAttRadius;
  float smoothFactor = clamp(1.0 - factor * factor, 0.0, 1.0);
  return smoothFactor * smoothFactor;
}
float GetDistAtt (float distSqr, float invSqrAttRadius) {
  float attenuation = 1.0 / max(distSqr, 0.01*0.01);
  attenuation *= SmoothDistAtt(distSqr , invSqrAttRadius);
  return attenuation;
}
float GetAngleAtt (vec3 L, vec3 litDir, float litAngleScale, float litAngleOffset) {
  float cd = dot(litDir, L);
  float attenuation = clamp(cd * litAngleScale + litAngleOffset, 0.0, 1.0);
  return (attenuation * attenuation);
}
float GGXMobile (float roughness, float NoH, vec3 H, vec3 N) {
  vec3 NxH = cross(N, H);
  float OneMinusNoHSqr = dot(NxH, NxH);
  float a = roughness * roughness;
  float n = NoH * a;
  float p = a / (OneMinusNoHSqr + n * n);
  return p * p;
}
float CalcSpecular (float roughness, float NoH, vec3 H, vec3 N) {
  return (roughness*0.25 + 0.25) * GGXMobile(roughness, NoH, H, N);
}
vec3 BRDFApprox (vec3 specular, float roughness, float NoV) {
  const vec4 c0 = vec4(-1.0, -0.0275, -0.572, 0.022);
  const vec4 c1 = vec4(1.0, 0.0425, 1.04, -0.04);
  vec4 r = roughness * c0 + c1;
  float a004 = min( r.x * r.x, exp2( -9.28 * NoV ) ) * r.x + r.y;
  vec2 AB = vec2( -1.04, 1.04 ) * a004 + r.zw;
  AB.y *= clamp(50.0 * specular.g, 0.0, 1.0);
  return specular * AB.x + AB.y;
}
vec3 CalcDynamicLighting (vec3 worldPos, vec3 N, vec3 V, vec3 diffuse, vec3 specular, float roughness) {
  vec3 lighting = vec3(0.0);
  vec3 diffuseContrib = diffuse / 3.14159265359;
  for (int i = 0; i < 2; i++) {
    vec3 PLU = cc_sphereLitPos[i].xyz - worldPos;
    vec3 PL = normalize(PLU);
    vec3 PH = normalize(PL + V);
    float PNL = max(dot(N, PL), 0.001);
    float PNH = max(dot(N, PH), 0.0);
    float distSqr = dot(PLU, PLU);
    float litRadius = cc_sphereLitSizeRange[i].x;
    float litRadiusSqr = litRadius * litRadius;
    float illum = 3.14159265359 * (litRadiusSqr / max(litRadiusSqr , distSqr));
    float attRadiusSqrInv = 1.0 / max(cc_sphereLitSizeRange[i].y, 0.01);
    attRadiusSqrInv *= attRadiusSqrInv;
    float att = GetDistAtt(distSqr, attRadiusSqrInv);
    vec3 lspec = specular * CalcSpecular(roughness, PNH, PH, N);
    lighting += PNL * cc_sphereLitColor[i].rgb * cc_sphereLitColor[i].w * illum * att * (diffuseContrib + lspec);
  }
  for (int i = 0; i < 2; i++) {
    vec3 SLU = cc_spotLitPos[i].xyz - worldPos;
    vec3 SL = normalize(SLU);
    vec3 SH = normalize(SL + V);
    float SNL = max(dot(N, SL), 0.001);
    float SNH = max(dot(N, SH), 0.0);
    float distSqr = dot(SLU, SLU);
    float litRadius = cc_spotLitSizeRangeAngle[i].x;
    float litRadiusSqr = litRadius * litRadius;
    float illum = 3.14159265359 * (litRadiusSqr / max(litRadiusSqr , distSqr));
    float attRadiusSqrInv = 1.0 / max(cc_spotLitSizeRangeAngle[i].y, 0.01);
    attRadiusSqrInv *= attRadiusSqrInv;
    float cosInner = max(dot(-cc_spotLitDir[i].xyz, SL), 0.01);
    float cosOuter = cc_spotLitSizeRangeAngle[i].z;
    float litAngleScale = 1.0 / max(0.001, cosInner - cosOuter);
    float litAngleOffset = -cosOuter * litAngleScale;
    float att = GetDistAtt(distSqr, attRadiusSqrInv);
    att *= GetAngleAtt(SL, -cc_spotLitDir[i].xyz, litAngleScale, litAngleOffset);
    vec3 lspec = specular * CalcSpecular(roughness, SNH, SH, N);
    lighting += SNL * cc_spotLitColor[i].rgb * cc_spotLitColor[i].w * illum * att * (diffuseContrib + lspec);
  }
  return lighting;
}
struct StandardSurface {
  vec4 albedo;
  vec3 position;
  vec3 normal;
  vec3 emissive;
  float roughness;
  float metallic;
  float occlusion;
};
vec4 CCStandardShading (StandardSurface s) {
  vec3 diffuse = s.albedo.rgb * (1.0 - s.metallic);
  vec3 specular = mix(vec3(0.04), s.albedo.rgb, s.metallic);
  vec3 N = normalize(s.normal);
  vec3 V = normalize(cc_cameraPos.xyz - s.position);
  vec3 L = normalize(-cc_mainLitDir.xyz);
  vec3 H = normalize(L+V);
  float NV = max(abs(dot(N, V)), 0.001);
  float NL = max(dot(N, L), 0.001);
  float NH = max(dot(N, H), 0.0);
  specular = BRDFApprox(specular, s.roughness, NV);
  vec3 diffuseContrib = diffuse / 3.14159265359;
  vec3 specularContrib = specular * CalcSpecular(s.roughness, NH, H, N);
  vec3 finalColor = NL * cc_mainLitColor.rgb * cc_mainLitColor.w * (diffuseContrib + specularContrib);
  finalColor += CalcDynamicLighting(s.position, N, V, diffuse, specular, s.roughness);
  float fAmb = 0.5 - N.y * 0.5;
  vec3 ambDiff = mix(cc_ambientSky.rgb, cc_ambientGround.rgb, fAmb) * cc_ambientSky.w;
  finalColor += (ambDiff.rgb * diffuse);
  #if CC_USE_IBL
    vec3 R = normalize(reflect(-V, N));
    vec4 envmap = fragTextureLod(cc_environment, R, s.roughness * cc_ambientGround.w);
    #if CC_USE_IBL == 2
      vec3 env = unpackRGBE(envmap);
    #else
      vec3 env = SRGBToLinear(envmap.rgb);
    #endif
    finalColor += env * cc_ambientSky.w * specular;
  #endif
  finalColor = finalColor * s.occlusion;
  #if CC_USE_HDR
    s.emissive *= cc_exposure.w;
  #endif
  finalColor += s.emissive;
  return vec4(finalColor, s.albedo.a);
}
vec3 ACESToneMap (vec3 color) {
  color = min(color, vec3(8.0));
  const float A = 2.51;
  const float B = 0.03;
  const float C = 2.43;
  const float D = 0.59;
  const float E = 0.14;
  return (color * (A * color + B)) / (color * (C * color + D) + E);
}
vec4 CCFragOutput (vec4 color) {
  #if !CC_USE_HDR
    color.rgb = sqrt(ACESToneMap(color.rgb));
  #endif
  return color;
}
uniform vec4 albedo;
uniform vec4 albedoScaleAndCutoff;
uniform vec4 pbrParams;
uniform vec4 emissive;
uniform vec4 emissiveScaleParam;
varying vec3 v_position;
varying vec2 v_uv;
varying vec2 v_uv1;
varying vec3 v_normal;
#if USE_VERTEX_COLOR
  varying vec3 v_color;
#endif
#if USE_ALBEDO_MAP
  uniform sampler2D albedoMap;
#endif
#if USE_NORMAL_MAP
  varying vec3 v_tangent;
  varying vec3 v_bitangent;
  uniform sampler2D normalMap;
#endif
#if USE_PBR_MAP
  uniform sampler2D pbrMap;
#endif
#if USE_METALLIC_ROUGHNESS_MAP
  uniform sampler2D metallicRoughnessMap;
#endif
#if USE_OCCLUSION_MAP
  uniform sampler2D occlusionMap;
#endif
#if USE_EMISSIVE_MAP
  uniform sampler2D emissiveMap;
#endif
#if USE_LIGHTMAP
  varying vec2 v_luv;
  uniform sampler2D cc_lightingMap;
#endif
#if USE_ALPHA_TEST
#endif
void surf (out StandardSurface s) {
  vec4 baseColor = albedo;
  #if USE_VERTEX_COLOR
    baseColor.rgb *= v_color;
  #endif
  #if USE_ALBEDO_MAP
    vec4 texColor = texture2D(albedoMap, ALBEDO_UV);
    texColor.rgb = SRGBToLinear(texColor.rgb);
    baseColor *= texColor;
  #endif
  s.albedo = baseColor;
  s.albedo.rgb *= albedoScaleAndCutoff.xyz;
  #if USE_ALPHA_TEST
    if (s.albedo.ALPHA_TEST_CHANNEL < albedoScaleAndCutoff.w) discard;
  #endif
  s.normal = v_normal;
  #if USE_NORMAL_MAP
    vec3 nmmp = texture2D(normalMap, NORMAL_UV).xyz - vec3(0.5);
    s.normal =
      (nmmp.x * pbrParams.w) * normalize(v_tangent) +
      (nmmp.y * pbrParams.w) * normalize(v_bitangent) +
      nmmp.z * normalize(s.normal);
  #endif
  s.position = v_position;
  vec4 pbr = pbrParams;
  #if USE_PBR_MAP
    vec4 res = texture2D(pbrMap, PBR_UV);
    pbr.x *= res.OCCLUSION_CHANNEL;
    pbr.y *= res.ROUGHNESS_CHANNEL;
    pbr.z *= res.METALLIC_CHANNEL;
  #endif
  #if USE_METALLIC_ROUGHNESS_MAP
    vec4 metallicRoughness = texture2D(metallicRoughnessMap, METALLIC_ROUGHNESS_UV);
    pbr.z *= metallicRoughness.METALLIC_CHANNEL;
    pbr.y *= metallicRoughness.ROUGHNESS_CHANNEL;
  #endif
  #if USE_OCCLUSION_MAP
    pbr.x *= texture2D(occlusionMap, OCCLUSION_UV).OCCLUSION_CHANNEL;
  #endif
  s.occlusion = clamp(pbr.x, 0.0, 0.96);
  s.roughness = clamp(pbr.y, 0.04, 1.0);
  s.metallic = pbr.z;
  s.emissive = emissive.rgb * emissiveScaleParam.xyz;
  #if USE_EMISSIVE_MAP
    s.emissive *= SRGBToLinear(texture2D(emissiveMap, EMISSIVE_UV).rgb);
  #endif
}
vec4 frag () {
  StandardSurface s; surf(s);
  vec4 color = CCStandardShading(s);
  #if USE_LIGHTMAP && !USE_BATCHING
    vec4 lighting = texture2D(cc_lightingMap, v_luv);
    float fAmb = 0.5 - s.normal.y * 0.5;
    vec3 ambDiff = mix(cc_ambientSky.rgb, cc_ambientGround.rgb, fAmb) * cc_ambientSky.w;
    vec3 diffuse = s.albedo.rgb * (1.0 - s.metallic);
    vec3 finalColor = (ambDiff.rgb * diffuse);
    finalColor += lighting.rgb * diffuse;
    finalColor = finalColor * s.occlusion;
    finalColor += s.emissive;
    color.rgb = lighting.a * finalColor + (1.0 - lighting.a) * color.rgb;
  #endif
  return CCFragOutput(color);
}
void main() { gl_FragColor = frag(); }

glsl1.vert:
precision highp float;
vec4 quaternionFromAxis (vec3 xAxis,vec3 yAxis,vec3 zAxis){
  mat3 m = mat3(xAxis,yAxis,zAxis);
  float trace = m[0][0] + m[1][1] + m[2][2];
  vec4 quat;
  if (trace > 0.) {
    float s = 0.5 / sqrt(trace + 1.0);
    quat.w = 0.25 / s;
    quat.x = (m[2][1] - m[1][2]) * s;
